    private void rbParpadeoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_rbParpadeoActionPerformed
        this.cmbTiempo.setVisible(true);
        this.botonEfectos1.setEfecto("Parpadeo");
        this.botonEfectos1.setIntervalo(Integer.parseInt(this.cmbTiempo.getSelectedItem().toString()));
    }//GEN-LAST:event_rbParpadeoActionPerformed

    private void cmbTiempoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cmbTiempoActionPerformed
        this.botonEfectos1.setIntervalo(Integer.parseInt(this.cmbTiempo.getSelectedItem().toString()));
    }//GEN-LAST:event_cmbTiempoActionPerformed

